import React, {useState, useEffect} from 'react'
import {View, Text, StyleSheet} from 'react-native'
import {TextInput, Button} from 'react-native-paper'

function Create() {

    const [title, setTitle] = useState("")
    const [body, setBody] = useState("")

    return (
        <View>
            <TextInput style = {styles.InputStyle} style ={{margin:15}}
            label = "Title"
            value = {title}
            mode="outlined"
            onChangeText = {text => setTitle(text)}/>
            <TextInput style = {styles.InputStyle} style ={{padding:15}}
            label = "Description"
            value = {body}
            multiline
            numberOfLines = {10}
            mode="outlined"
            onChangeText = {text => setBody(text)}/>
            <Button
            style ={{margine:10}}
            icon = "pencil"
            mode="contained"
            onPress={()=> console.log("pressed")}
            /><Text>Insert Article</Text> <Button/>
        </View>
        
    )
}

const styles = StyleSheet.create({
    inputStyle: {
        padding:10,
        marginTop:30,
    }
})

export default Create
